mapper.select:
select id, elder, item_name, amount, remark, expense_date, created_at, updated_at
from expenses
where id = #{id}

mapper.update:
update expenses
elder = #{elder}, item_name = #{itemName}, amount = #{amount}, remark = #{remark}, expense_date = #{expenseDate}, updated_at = #{updatedAt}
where id = #{id}
